import React, { useState } from 'react';
import api from '../api';
import { useNavigate } from 'react-router-dom';


export default function Login(){
const [email, setEmail] = useState('');
const [password, setPassword] = useState('');
const [error, setError] = useState(null);
const navigate = useNavigate();


const handle = async (e) => {
e.preventDefault();
try{
await api.post('/auth/login', { email, password });
navigate('/');
} catch(err){
setError(err.response?.data?.message || 'Erro');
}
};


return (
<div className="container">
<div className="card">
<h2>Login</h2>
{error && <div style={{color:'red'}}>{error}</div>}
<form onSubmit={handle}>
<div className="form-row">
<input className="input" value={email} onChange={e=>setEmail(e.target.value)} placeholder="E-mail" />
</div>
<div className="form-row">
<input className="input" type="password" value={password} onChange={e=>setPassword(e.target.value)} placeholder="Senha" />
</div>
<button type="submit">Entrar</button>
</form>
</div>
</div>
);
}
