import React, { useEffect, useState } from "react";
import api from "../../api";
import { Link } from "react-router-dom";

export default function StudentsList() {
  const [students, setStudents] = useState([]);

  const loadStudents = async () => {
    try {
      const res = await api.get("/students");
      setStudents(res.data);
    } catch (err) {
      console.error("Erro ao carregar alunos", err);
    }
  };

  useEffect(() => {
    loadStudents();
  }, []);

  const deleteStudent = async (id) => {
    if (!window.confirm("Deseja excluir este aluno?")) return;
    try {
      await api.delete(`/students/${id}`);
      loadStudents();
    } catch (err) {
      console.error("Erro ao excluir aluno", err);
    }
  };

  return (
    <div>
      <h2>Alunos</h2>
      <Link to="/students/new">Adicionar Aluno</Link>
      <ul>
        {students.map((s) => (
          <li key={s.id}>
            {s.name} — {s.email} — Curso: {s.course?.name}
            <Link to={`/students/edit/${s.id}`}> Editar </Link>
            <button onClick={() => deleteStudent(s.id)}>Excluir</button>
          </li>
        ))}
      </ul>
    </div>
  );
}
