import React, { useState, useEffect } from 'react';
import api from '../api';

export default function Header(){
  const [user, setUser] = useState(null);
  const [theme, setTheme] = useState(() => localStorage.getItem('theme') || 'light');

  useEffect(() => {
    api.get('/auth/me')
      .then(res => setUser(res.data))
      .catch(() => setUser(null));
  }, []);

  useEffect(() => {
    document.documentElement.classList.toggle('theme-dark', theme === 'dark');
    localStorage.setItem('theme', theme);
  }, [theme]);

  const toggleTheme = () => setTheme(prev => prev === 'dark' ? 'light' : 'dark');

  return (
    <header>
      <div className="container">
        <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', flexWrap: 'wrap', gap: '1rem' }}>
          <div style={{ display: 'flex', alignItems: 'center', gap: '1rem' }}>
            <div className="brand">üìä <strong>Gerenciamento Pro</strong></div>
            <div style={{ color: 'rgba(255,255,255,0.85)', fontSize: '0.95rem' }}>Organize cursos, alunos e produtos com facilidade</div>
          </div>

          <div style={{ display: 'flex', alignItems: 'center', gap: '0.75rem' }}>
            <button
              onClick={toggleTheme}
              className="btn-secondary"
              style={{ padding: '0.45rem 0.8rem' }}
              aria-label="Alternar tema"
            >
              {theme === 'dark' ? 'üåô Escuro' : '‚òÄÔ∏è Claro'}
            </button>

            {user && (
              <div style={{ color: 'white', fontSize: '0.95rem', textAlign: 'right' }}>
                <p style={{ margin: 0 }}>Bem-vindo, <strong>{user.name}</strong></p>
                <p style={{ margin: '0.25rem 0 0 0', opacity: 0.9 }}>
                  <span className="badge" style={{ display: 'inline-block', background: 'rgba(255,255,255,0.12)', color: 'white' }}>
                    {user.role === 'admin' ? 'üëë Administrador' : 'üë§ Usu√°rio'}
                  </span>
                </p>
              </div>
            )}
          </div>
        </div>
      </div>
    </header>
  );
}
