import React, { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import api from '../api';

export default function Navbar(){
  const navigate = useNavigate();
  const [loading, setLoading] = useState(false);

  const handleLogout = async () => {
    try {
      setLoading(true);
      await api.post('/auth/logout');
      navigate('/login');
    } catch (err) {
      console.error('Erro ao desconectar:', err);
    } finally {
      setLoading(false);
    }
  };

  const navItems = [
    { path: '/', label: 'üè† Home' },
    { path: '/students', label: 'üë®‚Äçüéì Alunos' },
    { path: '/courses', label: 'üìö Cursos' },
    { path: '/products', label: 'üõçÔ∏è Produtos' },
    { path: '/users', label: 'üë• Usu√°rios' }
  ];

  return (
    <nav className="container">
      <div style={{ display: 'flex', alignItems: 'center', gap: '2rem', flexWrap: 'wrap', width: '100%' }}>
        <div style={{ display: 'flex', alignItems: 'center', gap: '1rem', flexWrap: 'wrap', flex: 1 }}>
          {navItems.map(item => (
            <Link key={item.path} to={item.path}>{item.label}</Link>
          ))}
        </div>
        <button 
          onClick={handleLogout} 
          className="btn-danger"
          disabled={loading}
          style={{ marginLeft: 'auto' }}
        >
          {loading ? '‚è≥ Saindo...' : 'üö™ Sair'}
        </button>
      </div>
    </nav>
  );
}
